#!/usr/bin/env node
/*jslint nodejs:true */

function keys(h) {
	var ks = [];

	for (var k in h) {
		ks.push(k);
	}

	return ks;
};

exports.keys = keys;

function values(h) {
	var vs = [];

	for (var k in h) {
		vs.push(h[k]);
	}

	return vs;
}

exports.values = values;

function test() {
	return keys({a:1}).indexOf("a") != -1 && values({a:1, b:2, c:3}).indexOf(3) != -1;
}

exports.test = test;